import Heading from "../../component/heading/Heading";
import Form from "../../component/form/Form";
import './docsimp.scss';
import React, { useState } from 'react';


function DocSimp() {

    const [inputValues, setInputValues] = useState({
        inputValue1: '',
        inputValue2: '',
        inputValue3: '',
    });

    const handleInputChange = (e) => {
        const { id, value } = e.target;

        if(id===1) {
            setInputValue1(value);
        } else if (id===2) {
            setInputValue2(value);
        } else {
            setInputValue3(value);
        }
    };

    return (
        <div className='DocSimp'>
            <Heading title={"Welcome to Legal-verse."} description={"Analyse order/contract between Petitioner and Respondent giving 5 most critical clauses,their potential risks and suggestive revisions"}/>
            <Form inp1 = {{id: 1, label: 'Petitioner', value: inputValue1, onChange:handleInputChange}} inp2 = {{id:2, label: 'Respondant', value: inputValue2, onChange:handleInputChange}}  inp3 = {{id3:3, label: 'Input Contract Details', value: inputValue3, onChange:handleInputChange}} ></Form>
        </div>
    );
}

export default DocSimp
