import Heading from "../../component/heading/Heading";
import Form from "../../component/form/Form";
import './docsimp.scss';
import React, { useState } from 'react';


function DocSimp() {

    const [inputValue, setInputValue] = useState('');

    const handleInputChange = (e) => {
        const { id, val } = e.target;

        // Use the 'id' to determine which input field to update
        this.setState({ [id]: val });
    };

    return (
        <div className='DocSimp'>
            <Heading title={"Welcome to Legal-verse."} description={"Analyse order/contract between Petitioner and Respondent giving 5 most critical clauses,their potential risks and suggestive revisions"}/>
            <Form inp1 = {{id: 1, label: 'Petitioner', val: inputValue, onChange:handleInputChange}} inp2 = {{id:2, label: 'Respondant', val: inputValue, onChange:handleInputChange}}  inp3 = {{id3:3, label: 'Input Contract Details', val: inputValue, onChange:handleInputChange}} ></Form>
        </div>
    );
}

export default DocSimp
